pragma solidity ^0.8.7;

import "hardhat/console.sol";
import "./Controls.sol";

contract Data is IData {
    event ReceivedControls(address indexed controls);

    struct Continent_Info {
        uint8 owner;
        uint8 troopBonus;
    }
    struct Territory_Info {
        uint8 owner;
        uint256 troops;
    }

    enum controlsAddressSent {
        TRUE,
        FALSE
    }
    Continent_Info[6] public s_continents;
    Territory_Info[] public s_territories;
    address private controls_address;
    controlsAddressSent public s_controlsSet;
    // uint8[42][6] public s_neighbours = [
    //     0: [1,3,29],
    //     1: [0,3,4,2],
    //     2: [1,4,5,13],
    //     3: [0,1,4,6],
    //     4: [1,2,3,5,6,7],
    //     5: [2,4,7],
    //     6: [3,4,7,8],
    //     7: [4,5,6,8],
    //     8: [6,7,9],
    //     9: [8,10,11],
    //     10: [9,11,12],
    //     11: [9,10,12,20],
    //     12: [10,11],
    //     13: [2,14,15],
    //     14: [13,15,16,17],
    //     15: [13,14,16,18],
    //     16: [14,15,17,18,19],
    //     17: [14,16,19,26,33,35],
    //     18: [15,16,19,20],
    //     19: [16,17,18,20,21,35],
    //     20: [11,18,19,21,22,23],
    //     21: [19,20,23,35],
    //     22: [20,21,23,24],
    //     23: [20,21,22,24,25,35],
    //     24: [22,23,25],
    //     25: [23,24],
    //     26: [17,27,33,34],
    //     27: [26,28,30,31,34],
    //     28: [27,30,29],
    //     29: [28,30,31,32,0],
    //     30: [27,28,29,31],
    //     31: [29,30,27,34,32],
    //     32: [29,31],
    //     33: [17,26,34,36,35],
    //     34: [31,27,26,33,36,37],
    //     35: [19,17,33,36,23,21],
    //     36: [35,33,34,37],
    //     37: [34,36,38],
    //     38: [37,39,40],
    //     39: [38,41,44],
    //     40: [38,39,41],
    //     41: [40,39],
    // ];

    // Array containing territories that are neighbours of the territory of the index. 99 is a filler.
    uint8[][] public s_neighbours = [
        [1, 3, 29, 99, 99, 99], //0
        [0, 3, 4, 2, 99, 99],
        [1, 4, 5, 13, 99, 99],
        [0, 1, 4, 6, 99, 99],
        [1, 2, 3, 5, 6, 7],
        [2, 4, 7, 99, 99, 99],
        [3, 4, 7, 8, 99, 99],
        [4, 5, 6, 8, 99, 99],
        [6, 7, 9, 99, 99, 99],
        [8, 10, 11, 99, 99, 99],
        [9, 11, 12, 99, 99, 99], //10
        [9, 10, 12, 20, 99, 99],
        [10, 11, 99, 99, 99, 99],
        [2, 14, 15, 99, 99, 99],
        [13, 15, 16, 17, 99, 99],
        [13, 14, 16, 18, 99, 99],
        [14, 15, 17, 18, 19, 99],
        [14, 16, 19, 26, 33, 35],
        [15, 16, 19, 20, 99, 99],
        [16, 17, 18, 20, 21, 35],
        [11, 18, 19, 21, 22, 23], //20
        [19, 20, 23, 35, 99, 99],
        [20, 21, 23, 24, 99, 99],
        [20, 21, 22, 24, 25, 35],
        [22, 23, 25, 99, 99, 99],
        [23, 24, 99, 99, 99, 99],
        [17, 27, 33, 34, 99, 99],
        [26, 28, 30, 31, 34, 99],
        [27, 30, 29, 99, 99, 99],
        [28, 30, 31, 32, 0, 99],
        [27, 28, 29, 31, 99, 99],
        [29, 30, 27, 34, 32, 99],
        [29, 31, 99, 99, 99, 99],
        [17, 26, 34, 36, 35, 99],
        [31, 27, 26, 33, 36, 37],
        [19, 17, 33, 36, 23, 21],
        [35, 33, 34, 37, 99, 99],
        [34, 36, 38, 99, 99, 99],
        [37, 39, 40, 99, 99, 99],
        [38, 41, 44, 99, 99, 99],
        [38, 39, 41, 99, 99, 99],
        [40, 39, 99, 99, 99, 99]
    ];

    modifier onlyControls() {
        require(msg.sender == controls_address);
        _;
    }

    constructor() {
        s_controlsSet = controlsAddressSent.FALSE;
    }

    function setControlsAddress(address controls) external {
        require(s_controlsSet == controlsAddressSent.FALSE);
        emit ReceivedControls(controls);
        controls_address = controls;
        s_controlsSet = controlsAddressSent.TRUE;
    }

    // Initializes continents array with owner -1 (indicates no owner), and the troop bonuses of each continent.
    // As player comes to own a contient, owner will be changed to the player.
    function initializeContinents() external onlyControls {
        for (uint8 i = 0; i < 6; i++) {
            s_continents[i].owner = 4; //@dev 0=p1, 1=p2, 2=p3, 3=p4, 4=available
            if (i == 0) {
                // North America
                s_continents[i].troopBonus = 5;
            } else if (i == 1) {
                // South America
                s_continents[i].troopBonus = 2;
            } else if (i == 2) {
                // Europe
                s_continents[i].troopBonus = 5;
            } else if (i == 3) {
                // Africa
                s_continents[i].troopBonus = 3;
            } else if (i == 4) {
                // Asia
                s_continents[i].troopBonus = 7;
            } else if (i == 5) {
                // Oceania
                s_continents[i].troopBonus = 2;
            }
        }
    }

    function updateContinentsLoop(
        uint loopStart,
        uint loopEnd,
        uint8 continent
    ) internal {
        uint8 owner;
        uint8 prevOwner;
        for (uint i = loopStart; i < loopEnd; i++) {
            owner = s_territories[i].owner;
            // if continent owner is not previous owner, the loop will break since owner doesn't own continent
            if (i != loopEnd && owner != prevOwner) {
                s_continents[continent].owner = 4;
                break;
                // if prevOwner == currentOwner, then the owner owns the continent
            } else if (i == loopEnd - 1 && owner == prevOwner) {
                s_continents[continent].owner = owner;
            }
            prevOwner = s_territories[i].owner;
        }
    }

    function updateContinents() external onlyControls {
        // North America
        updateContinentsLoop(0, 9, 0);
        // South America
        updateContinentsLoop(9, 13, 1);
        // Europe
        updateContinentsLoop(13, 20, 2);
        // Africa
        updateContinentsLoop(20, 26, 3);
        // Asia
        updateContinentsLoop(26, 38, 4);
        // Australia
        updateContinentsLoop(38, 42, 5);
    }

    function getAllTerritories() public view returns (Territory_Info[] memory) {
        return s_territories;
    }

    function getTerritories(uint territoryId)
        public
        view
        returns (Territory_Info memory)
    {
        return s_territories[territoryId];
    }

    /* Controls Functions */
    function getContinentBonus(uint continent) external view returns (uint8) {
        return s_continents[continent].troopBonus;
    }

    function getContinentOwner(uint continent) external view returns (uint8) {
        return s_continents[continent].owner;
    }

    function getContinentInfo() public view returns (Continent_Info[6] memory) {
        return s_continents;
    }

    function pushToTerritories(uint8 playerAwarded) external onlyControls {
        s_territories.push(Territory_Info(playerAwarded, 1));
    }

    function addTroopToTerritory(uint index) external onlyControls {
        s_territories[index].troops++;
    }

    function removeTroopFromTerritory(uint index) external onlyControls {
        s_territories[index].troops--;
    }

    function getNeighbours(uint territory)
        external
        view
        onlyControls
        returns (uint8[] memory)
    {
        return s_neighbours[territory];
    }

    function getTerritoryOwner(uint j) public view returns (uint8 owner) {
        return s_territories[j].owner;
    }

    function getTroopCount(uint territory) public view returns (uint256) {
        return s_territories[territory].troops;
    }
}
","keccak256":"0xd0b6b7ecab30b4ef67b85700194ff6671b3ba48214d6f09c033b4a39d0e2b548","license":"MIT"},"contracts/Main.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;
import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";
import "hardhat/console.sol";

/**@title Cryptorisk Main Contract
 * @author Michael King and Mitchell Spencer
 * @dev Implements the Chainlink VRF V2
 */

interface IControls {
    function set_players(address payable[] memory) external;

    function push_to_territories(uint8 playerAwarded) external;

    function get_territory_owner(uint) external returns (uint);

    function add_troop_to_territory(uint) external;

    function set_main_address(address main) external;

    function deploy_control(uint8 amountToDeploy, uint8 location)
        external
        returns (bool);

    function attack_control(
        uint8 territoryOwned,
        uint8 territoryAttacking,
        uint256 troopQuantity
    ) external;

    function fortify_control() external;

    function get_troops_to_deploy() external view returns (uint8);

    function getPlayerTurn() external view returns (address);
}

contract Main is VRFConsumerBaseV2 {
    /* Type declarations */
    enum TerritoryState {
        INCOMPLETE,
        COMPLETE
    }
    enum LobbyState {
        OPEN,
        CLOSED
    }
    enum GameState {
        DEPLOY,
        ATTACK,
        FORTIFY,
        INACTIVE
    }

    enum mainAddressSent {
        TRUE,
        FALSE
    }

    /* Variables */
    // Chainlink VRF Variables
    VRFCoordinatorV2Interface private immutable i_vrfCoordinator;
    uint64 private immutable i_subscriptionId;
    bytes32 private immutable i_gasLane;
    uint32 private immutable i_callbackGasLimit;
    uint16 private constant REQUEST_CONFIRMATIONS = 3;

    // Setup Variables
    uint256 private immutable i_entranceFee;
    address private immutable controls_address;
    address private immutable data_address;

    uint8[4] private territoriesAssigned = [0, 0, 0, 0]; // Used to track if player receives enough territory.
    uint256[] s_randomWordsArray;
    TerritoryState public s_territoryState;
    GameState public s_gameState;
    LobbyState public s_lobbyState;
    address payable[] public s_players;
    address public player_turn;
    mainAddressSent public s_mainSet;

    /* Events */
    event RequestedTerritoryRandomness(uint256 indexed requestId);
    event RequestedTroopRandomness(uint256 indexed requestId);
    event ReceivedRandomWords();
    event GameSetupComplete();
    event PlayerJoinedLobby(address indexed player);
    event GameStarting();

    constructor(
        address vrfCoordinatorV2,
        uint64 subscriptionId,
        bytes32 gasLane, // keyHash
        uint32 callbackGasLimit,
        uint256 entranceFee,
        address controls,
        address data
    ) VRFConsumerBaseV2(vrfCoordinatorV2) {
        i_vrfCoordinator = VRFCoordinatorV2Interface(vrfCoordinatorV2);
        i_subscriptionId = subscriptionId;
        i_gasLane = gasLane;
        i_callbackGasLimit = callbackGasLimit;
        i_entranceFee = entranceFee;
        s_lobbyState = LobbyState.OPEN;
        s_gameState = GameState.INACTIVE;
        s_mainSet = mainAddressSent.FALSE;
        controls_address = controls;
        data_address = data;
    }

    /* Modifiers */

    modifier onlyPlayer() {
        require(msg.sender == IControls(controls_address).getPlayerTurn());
        _;
    }

    /* Functions */

    function enterLobby() public payable {
        require(msg.value >= i_entranceFee, "Send More to Enter Lobby");
        require(s_lobbyState == LobbyState.OPEN, "Lobby is full"); // require or if statement?
        s_players.push(payable(msg.sender));
        emit PlayerJoinedLobby(msg.sender);
        if (s_players.length == 4) {
            s_lobbyState = LobbyState.CLOSED;
            emit GameStarting();
            requestRandomness(42);
        }
    }

    // call this function as soon as contract is deployed
    function setMainAddress() public {
        require(
            s_mainSet == mainAddressSent.FALSE,
            "Controls contract has already received Main address"
        );
        IControls(controls_address).set_main_address(address(this));
        s_mainSet = mainAddressSent.TRUE;
    }

    function requestRandomness(uint32 num_words) private {
        uint256 requestId = i_vrfCoordinator.requestRandomWords(
            i_gasLane,
            i_subscriptionId,
            REQUEST_CONFIRMATIONS,
            i_callbackGasLimit,
            num_words
        );
        if (s_territoryState == TerritoryState.COMPLETE) {
            emit RequestedTroopRandomness(requestId);
        } else {
            emit RequestedTerritoryRandomness(requestId);
        }
    }

    function fulfillRandomWords(
        uint256, /* requestId */
        uint256[] memory randomWords
    ) internal override {
        s_randomWordsArray = randomWords;
        emit ReceivedRandomWords();
        if (s_territoryState == TerritoryState.INCOMPLETE) {
            assignTerritory(randomWords);
        } else {
            assignTroops(randomWords);
        }
    }

    /**
     * Function receives array of 42 random words which are then used to assign each territory (0-41) an owner (0-3).
     * Mutates a globally declared array s_territories.
     */
    function assignTerritory(uint256[] memory randomWords) private {
        uint8[4] memory playerSelection = [0, 1, 2, 3]; // Eligible players to be assigned territory, each is popped until no players left to receive.
        uint8 territoryCap = 10; // Initial cap is 10, moves up to 11 after two players assigned 10.
        uint8 remainingPlayers = 4; // Ticks down as players hit their territory cap
        uint256 indexAssignedTerritory; // Index of playerSelection that contains a list of eligible players to receive territory.
        uint8 playerAwarded; // Stores the player to be awarded territory, for pushing into the s_territories array.'
        for (uint i = 0; i < randomWords.length; i++) {
            indexAssignedTerritory = randomWords[i] % remainingPlayers; // Calculates which index from playerSelection will receive the territory
            playerAwarded = playerSelection[indexAssignedTerritory]; // Player to be awarded territory
            IControls(controls_address).push_to_territories(playerAwarded);
            territoriesAssigned[playerAwarded]++;
            if (territoriesAssigned[playerAwarded] == territoryCap) {
                delete playerSelection[playerAwarded]; // Removes awarded player from the array upon hitting territory cap.
                remainingPlayers--;
                if (remainingPlayers == 2) {
                    territoryCap = 11; // Moves up instead of down, to remove situation where the cap goes down and we have players already on the cap then receiving too much territory.
                }
            }
        }
        s_territoryState = TerritoryState.COMPLETE;
        requestRandomness(78);
    }

    function assignTroops(uint256[] memory randomWords) private {
        uint randomWordsIndex = 0;
        // s_territories.length == 42
        // playerTerritoryIndexes.length == 10 or 11
        for (uint i = 0; i < 4; i++) {
            uint[] memory playerTerritoryIndexes = new uint[](
                territoriesAssigned[i]
            ); // Initializes array of indexes for territories owned by player i
            uint index = 0;
            for (uint j = 0; j < 42; j++) {
                if (IControls(controls_address).get_territory_owner(j) == i) {
                    playerTerritoryIndexes[index++] = j;
                }
            }

            for (uint j = 0; j < 30 - territoriesAssigned[i]; j++) {
                uint territoryAssignedTroop = randomWords[randomWordsIndex++] %
                    territoriesAssigned[i];
                IControls(controls_address).add_troop_to_territory(
                    playerTerritoryIndexes[territoryAssignedTroop]
                );
            }
        }
        emit GameSetupComplete();
        s_gameState = GameState.DEPLOY;
        IControls(controls_address).set_players(s_players);
    }

    function deploy(uint8 amountToDeploy, uint8 location) public onlyPlayer {
        require(
            s_gameState == GameState.DEPLOY,
            "It is currently not deploy phase!"
        );
        require(
            amountToDeploy <=
                IControls(controls_address).get_troops_to_deploy(),
            "You do not have enough troops!"
        );

        if (
            IControls(controls_address).deploy_control(amountToDeploy, location)
        ) {
            s_gameState = GameState.ATTACK;
        }
    }

    function attack(
        uint8 territoryOwned,
        uint8 territoryAttacking,
        uint256 troopQuantity
    ) public onlyPlayer {
        require(
            s_gameState == GameState.ATTACK,
            "It is currently not attack phase."
        );
        IControls(controls_address).attack_control(
            territoryOwned,
            territoryAttacking,
            troopQuantity
        );
        s_gameState = GameState.FORTIFY;
    }

    function fortify() public onlyPlayer {
        require(
            s_gameState == GameState.FORTIFY,
            "It is currently not fortify phase."
        );
        IControls(controls_address).fortify_control();
        s_gameState = GameState.DEPLOY;
    }

    /** Getter Functions */

    function getRandomWordsArray() public view returns (uint256[] memory) {
        return s_randomWordsArray;
    }

    function getRandomWordsArrayIndex(uint256 index)
        public
        view
        returns (uint256)
    {
        return s_randomWordsArray[index];
    }

    function getSubscriptionId() public view returns (uint64) {
        return i_subscriptionId;
    }

    function getGasLane() public view returns (bytes32) {
        return i_gasLane;
    }

    function getCallbackGasLimit() public view returns (uint32) {
        return i_callbackGasLimit;
    }

    function getTerritoryState() public view returns (TerritoryState) {
        return s_territoryState;
    }

    function getRequestConfirmations() public pure returns (uint256) {
        return REQUEST_CONFIRMATIONS;
    }

    function getPlayer(uint256 index) public view returns (address) {
        return s_players[index];
    }

    function getNumberOfPlayers() public view returns (uint256) {
        return s_players.length;
    }

    function getEntranceFee() public view returns (uint256) {
        return i_entranceFee;
    }

    function getLobbyState() public view returns (LobbyState) {
        return s_lobbyState;
    }
}
