// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";
import "hardhat/console.sol";
import "./Main.sol";

interface IData {
    function initializeContinents() external;

    function getContinentOwner(uint256 continent) external view returns (uint8);

    function getContinentBonus(uint256 continent) external view returns (uint8);

    function pushToTerritories(uint8) external;

    function addTroopToTerritory(uint256 index) external;

    function updateContinents() external;

    function setControlsAddress(address controls) external;

    function getNeighbours(uint256 territory) external view returns (uint8[] memory);

    function getTerritoryOwner(uint256) external returns (uint8);

    function getTroopCount(uint256 territory) external view returns (uint256);

    function removeTroopFromTerritory(uint256 index) external;

    function changeOwner(uint256 territory, uint8 newOwner) external;

    function resetData() external;
}

contract Controls is IControls, VRFConsumerBaseV2 {
    event CurrentPlayer(address indexed player);
    event ReceivedMain(address indexed main);
    event PlayerDeploying(address indexed player);
    event PlayerAttacking(address indexed player);
    event PlayerFortifying(address indexed player);
    event DiceRolled();
    event RollingDice(uint256 indexed s_requestId);
    event GameOver(address indexed winner);
    event TransferTroopsAvailable(uint256 indexed territoryBeingAttacked);

    // enums

    enum mainAddressSent {
        TRUE,
        FALSE
    }

    // Chainlink VRF Variables
    VRFCoordinatorV2Interface private immutable i_vrfCoordinator;
    uint64 private immutable i_subscriptionId;
    bytes32 private immutable i_gasLane;
    uint32 private immutable i_callbackGasLimit;
    uint16 private constant REQUEST_CONFIRMATIONS = 3;

    // variables
    uint256 s_recentAttackingArmies;
    uint256 s_recentDefendingArmies;
    uint8 s_recentTerritoryAttacking;
    uint8 s_recentTerritoryBeingAttacked;

    address private main_address;
    address private data_address;

    mainAddressSent public s_mainSet;
    uint256 public s_requestId;
    uint8 public s_troopsToDeploy;
    uint8 public s_playerTurn;

    address payable[] s_playersArray;
    uint256[] s_diceWords;

    bool s_attackSuccess;
    bool s_gameIsOver;

    constructor(
        address vrfCoordinatorV2,
        uint64 subscriptionId,
        bytes32 gasLane,
        uint32 callbackGasLimit,
        address data
    ) VRFConsumerBaseV2(vrfCoordinatorV2) {
        i_vrfCoordinator = VRFCoordinatorV2Interface(vrfCoordinatorV2);
        i_subscriptionId = subscriptionId;
        i_gasLane = gasLane;
        i_callbackGasLimit = callbackGasLimit;
        data_address = data;
        s_mainSet = mainAddressSent.FALSE;
        s_playerTurn = 3;
        s_troopsToDeploy = 0;
        s_attackSuccess = false;
        s_gameIsOver = false;
    }

    modifier onlyMain() {
        require(msg.sender == main_address);
        _;
    }

    modifier gameIsOver() {
        require(s_gameIsOver == true);
        _;
    }

    function set_main_address(address main) external {
        require(s_mainSet == mainAddressSent.FALSE);
        emit ReceivedMain(main);
        main_address = main;
        s_mainSet = mainAddressSent.TRUE;
        IData(data_address).setControlsAddress(address(this));
    }

    function set_players(address payable[] memory players) external onlyMain {
        s_playersArray = players;
        IData(data_address).initializeContinents();
        next_player();
    }

    function next_player() private {
        s_playerTurn++;
        if (s_playerTurn == s_playersArray.length) {
            s_playerTurn = 0;
        }
        IData(data_address).updateContinents();
        s_troopsToDeploy = 0;
        for (uint256 c = 0; c < 6; c++) {
            if (IData(data_address).getContinentOwner(c) == s_playerTurn) {
                s_troopsToDeploy += IData(data_address).getContinentBonus(c);
            }
        }

        uint8 totalTerritories = 0;
        for (uint256 i = 0; i < 42; i++) {
            if (IData(data_address).getTerritoryOwner(i) == s_playerTurn) {
                totalTerritories++;
            }
        }
        if (totalTerritories < 9) {
            s_troopsToDeploy += 3;
        } else {
            s_troopsToDeploy += totalTerritories / 3;
        }
    }

    function deploy_control(uint8 amountToDeploy, uint8 location) external onlyMain returns (bool) {
        require(IData(data_address).getTerritoryOwner(location) == s_playerTurn, "You do not own this territory");
        console.log("We have: ", s_troopsToDeploy, "troops to deploy");
        console.log("Inside deploy_control, deploying ", amountToDeploy, " troops, at territory ", location);
        emit PlayerDeploying(s_playersArray[s_playerTurn]);
        for (uint256 i = 0; i < amountToDeploy; i++) {
            IData(data_address).addTroopToTerritory(location);
        }
        s_troopsToDeploy -= amountToDeploy;
        console.log(s_troopsToDeploy);
        if (s_troopsToDeploy == 0) {
            return false; // returns false if all troops have not been deployed
        }
        return true;
    }

    function attack_control(
        uint8 territoryOwned,
        uint8 territoryAttacking,
        uint256 attackingArmies // could
    ) external onlyMain {
        require(
            validate_attackable(territoryOwned, territoryAttacking),
            "Territory you are trying to attack is not a neighbour!"
        );
        require(
            (attackingArmies < IData(data_address).getTroopCount(territoryOwned)) && (attackingArmies > 0),
            "You cannot attack with that many troops!"
        );
        emit PlayerAttacking(s_playersArray[s_playerTurn]);

        uint256 defendingArmies = IData(data_address).getTroopCount(territoryAttacking);
        if (defendingArmies >= 2) {
            defendingArmies = 2;
        } else {
            defendingArmies = 1;
        }
        uint8 num_words = getArmies(attackingArmies, defendingArmies); // attackingArmies, defendingArmies not returning correct numbers
        s_recentTerritoryAttacking = territoryOwned;
        s_recentTerritoryBeingAttacked = territoryAttacking;
        s_recentAttackingArmies = attackingArmies;
        s_recentDefendingArmies = defendingArmies;
        diceRoller(num_words);

        // 1. Player clicks on their own territory
        // 2. Player clicks on enemy territory.
        // 3. Player chooses how many troops to attack with.
        // 4. Player attacks
    }

    function battle(
        uint256 attackingArmies,
        uint256 defendingArmies,
        uint256 territoryAttacking,
        uint256 territoryBeingAttacked,
        uint256[] memory randomWords
    ) private {
        console.log("Inside the battle function.");

        uint256[] memory attackerRolls = new uint256[](attackingArmies);
        uint256[] memory defenderRolls = new uint256[](defendingArmies);
        for (uint256 i = 0; i < (attackingArmies + defendingArmies); i++) {
            if (i < attackingArmies) {
                attackerRolls[i] = randomWords[i] % 6;
            } else {
                defenderRolls[i - attackingArmies] = randomWords[i] % 6;
            }
        }

        // Sorting the two rolls arrays
        insertionSort(attackerRolls);
        insertionSort(defenderRolls);

        console.log("Attacker die rolls are: ");
        for (uint256 i = 0; i < attackerRolls.length; i++) {
            console.log(attackerRolls[i]);
        }
        console.log("Defender die rolls are: ");
        for (uint256 i = 0; i < defenderRolls.length; i++) {
            console.log(defenderRolls[i]);
        }

        uint256 attacks; // either 1 or 2
        if (attackingArmies > defendingArmies) {
            attacks = defendingArmies;
        } else {
            attacks = attackingArmies;
        }
        console.log("The number of attacks will be: ", attacks);
        for (uint256 i = 0; i < attacks; i++) {
            if (attackerRolls[i] > defenderRolls[i]) {
                console.log("The attacker won an attack.");
                // 3 v 1 , 2 v 1 , 1 v 1, 2 v 2, 2 v 1, 1 v 1 //
                // attacker wins, defender dies
                IData(data_address).removeTroopFromTerritory(territoryBeingAttacked);
                console.log(
                    "troops left in defedning territory: ",
                    IData(data_address).getTroopCount(territoryBeingAttacked)
                );
                if (
                    // Attacker has killed all troops in the defending territory
                    IData(data_address).getTroopCount(territoryBeingAttacked) == 0
                ) {
                    // Territory now becomes Attackers
                    IData(data_address).changeOwner(territoryBeingAttacked, s_playerTurn);
                    IData(data_address).removeTroopFromTerritory(territoryAttacking);
                    IData(data_address).addTroopToTerritory(territoryBeingAttacked);
                    // Attacker can select how many troops he wants to deploy to territory
                    s_attackSuccess = true;
                    uint256 defeatedPlayer = IData(data_address).getTerritoryOwner(territoryBeingAttacked);
                    if (getTotalTroops(defeatedPlayer) == 0) {
                        // if that was the last armies of the player
                        killPlayer(defeatedPlayer); // player is removed from the game
                    }
                    if (s_playersArray.length == 1) {
                        gameOver();
                        s_gameIsOver = true;
                    } else {
                        emit TransferTroopsAvailable(territoryBeingAttacked);
                        console.log("can transfer troops to", territoryBeingAttacked);
                    }
                }
            } else {
                // defender wins
                console.log("The defender won an attack.");
                IData(data_address).removeTroopFromTerritory(territoryAttacking);
            }
        }
    }

    // This is a function that is executed when a button is clicked.
    function troopTransferAfterAttack(uint256 amountOfTroops) public {
        require(s_attackSuccess);
        require(s_playersArray[s_playerTurn] == msg.sender);
        require(
            amountOfTroops < IData(data_address).getTroopCount(s_recentTerritoryAttacking) && (amountOfTroops > 0),
            "You cannot move that amount of troops!"
        );

        for (uint256 i = 0; i < amountOfTroops; i++) {
            IData(data_address).addTroopToTerritory(s_recentTerritoryBeingAttacked);
            IData(data_address).removeTroopFromTerritory(s_recentTerritoryAttacking);
        }
        s_attackSuccess = false;
    }

    function diceRoller(uint32 num_words) private {
        s_requestId = i_vrfCoordinator.requestRandomWords(
            i_gasLane,
            i_subscriptionId,
            REQUEST_CONFIRMATIONS,
            i_callbackGasLimit,
            num_words
        );
        emit RollingDice(s_requestId);
    }

    function fulfillRandomWords(
        uint256, /* requestId */
        uint256[] memory randomWords
    ) internal override {
        s_diceWords = randomWords;
        emit DiceRolled();
        battle(
            s_recentAttackingArmies,
            s_recentDefendingArmies,
            s_recentTerritoryAttacking,
            s_recentTerritoryBeingAttacked,
            randomWords
        );
    }

    function transferTroops(
        uint8 territoryMovingFrom,
        uint8 territoryMovingTo,
        uint256 troopsMoving
    ) public {
        require(
            (troopsMoving < IData(data_address).getTroopCount(territoryMovingFrom)) && (troopsMoving > 0),
            "You cannot move that amount of troops!"
        );

        for (uint256 i = 0; i < troopsMoving; i++) {
            IData(data_address).addTroopToTerritory(territoryMovingTo);
            IData(data_address).removeTroopFromTerritory(territoryMovingFrom);
        }
    }

    function fortify_control(
        uint8 territoryMovingFrom,
        uint8 territoryMovingTo,
        uint256 troopsMoving
    ) external onlyMain returns (bool) {
        console.log("inside fortify_control");
        //need to add parameters
        emit PlayerFortifying(s_playersArray[s_playerTurn]);
        require(
            validateFortifiable(territoryMovingFrom, territoryMovingTo),
            "Territory you are trying move troops to is not one of your neighbours!"
        );
        transferTroops(territoryMovingFrom, territoryMovingTo, troopsMoving);

        next_player();
        return true;
    }

    function validate_owner(uint8 territory_clicked) internal returns (bool) {
        uint8 territory_owner = IData(data_address).getTerritoryOwner(territory_clicked);
        if (territory_owner == s_playerTurn) {
            return true;
        } else {
            return false;
        }
    }

    function validateFortifiable(uint8 territoryMovingFrom, uint8 territoryMovingTo) internal returns (bool) {
        require(IData(data_address).getTroopCount(territoryMovingFrom) > 1, "You must have more than 1 troop to move!");
        console.log(territoryMovingFrom, territoryMovingTo);
        console.log(
            IData(data_address).getTerritoryOwner(territoryMovingFrom),
            IData(data_address).getTerritoryOwner(territoryMovingTo)
        );
        require(
            validate_owner(territoryMovingFrom) && validate_owner(territoryMovingTo),
            "You must own both territories to move troops!"
        );
        uint8[] memory neighbours = IData(data_address).getNeighbours(territoryMovingFrom);
        for (uint256 i = 0; i < 6; i++) {
            if ((territoryMovingTo == neighbours[i])) {
                return true;
            }
        }
        return false;
    }

    function validate_attackable(uint8 territoryOwned, uint8 territoryAttacking) internal returns (bool) {
        require(
            IData(data_address).getTroopCount(territoryOwned) > 1,
            "You must have at least 1 troop remaining in your territory to attack!"
        );
        require(!validate_owner(territoryAttacking), "You cannot attack your own territory!"); //checks if the player owns the territory they are trying to attack
        uint8[] memory neighbours = IData(data_address).getNeighbours(territoryOwned);
        for (uint256 i = 0; i < 6; i++) {
            if ((territoryAttacking == neighbours[i])) {
                return true;
            }
        }
        return false;
    }

    function getArmies(uint256 attackingArmies, uint256 defendingArmies) private pure returns (uint8) {
        uint8 num_words = 0;
        if (attackingArmies == 3) {
            num_words = 3;
        } else if (attackingArmies == 2) {
            num_words = 2;
        } else {
            num_words = 1;
        }
        if (defendingArmies == 2) {
            num_words += 2;
        } else {
            num_words += 1;
        }
        return num_words;
    }

    function gameOver() public gameIsOver {
        address winner = s_playersArray[s_playerTurn];
        emit GameOver(winner);
        (bool success, ) = main_address.call(abi.encodeWithSignature("payWinner(address)", winner));
        require(success, "call to main failed");
        IData(data_address).resetData();
        resetControls();
    }

    function resetControls() private {
        s_playerTurn = 3;
        s_troopsToDeploy = 0;
        s_attackSuccess = false;
        s_gameIsOver = false;
        s_playersArray = new address payable[](0);
    }

    function add_troop_to_territory(uint256 index) external onlyMain {
        IData(data_address).addTroopToTerritory(index);
    }

    function killPlayer(uint256 deadPlayer) private {
        delete s_playersArray[deadPlayer];
    }

    function getTotalTroops(uint256 player) public returns (uint256) {
        uint256 totalTroops = 0;
        for (uint256 i = 0; i < 42; i++) {
            if (IData(data_address).getTerritoryOwner(i) == player) {
                totalTroops += IData(data_address).getTroopCount(i);
            }
        }
        return totalTroops;
    }

    function get_territory_owner(uint256 j) external onlyMain returns (uint256) {
        return IData(data_address).getTerritoryOwner(j);
    }

    function get_troops_to_deploy() public view returns (uint8) {
        return s_troopsToDeploy;
    }

    function getPlayerTurn() public view returns (address) {
        return s_playersArray[s_playerTurn];
    }

    function getRequestId() public view returns (uint256) {
        return s_requestId;
    }

    function push_to_territories(uint8 playerAwarded) external onlyMain {
        IData(data_address).pushToTerritories(playerAwarded);
    }

    function getAttackStatus() public view returns (bool) {
        return s_attackSuccess;
    }

    function insertionSort(uint256[] memory arr) private pure {
        uint256 i;
        uint256 key;
        int256 j;
        for (i = 1; i < arr.length; i++) {
            key = arr[i];
            j = int256(i - 1);
            while (j >= 0 && arr[uint256(j)] < key) {
                arr[uint256(j + 1)] = arr[uint256(j)];
                j = j - 1;
            }
            arr[uint256(j + 1)] = key;
        }
    }
}

contracts/Main.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;
import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";
import "hardhat/console.sol";

/**@title Cryptorisk Main Contract
 * @author Michael King and Mitchell Spencer
 * @dev Implements the Chainlink VRF V2
 */

interface IControls {
    function set_players(address payable[] memory) external;

    function push_to_territories(uint8 playerAwarded) external;

    function get_territory_owner(uint256) external returns (uint256);

    function add_troop_to_territory(uint256) external;

    function set_main_address(address main) external;

    function deploy_control(uint8 amountToDeploy, uint8 location) external returns (bool);

    function attack_control(
        uint8 territoryOwned,
        uint8 territoryAttacking,
        uint256 troopQuantity
    ) external;

    function fortify_control(
        uint8 territoryMovingFrom,
        uint8 territoryMovingTo,
        uint256 troopsMoving
    ) external returns (bool);

    function get_troops_to_deploy() external view returns (uint8);

    function getPlayerTurn() external view returns (address);

    function getAttackStatus() external view returns (bool);
}

contract Main is VRFConsumerBaseV2 {
    /* Type declarations */
    enum TerritoryState {
        INCOMPLETE,
        COMPLETE
    }
    enum LobbyState {
        OPEN,
        CLOSED
    }
    enum GameState {
        DEPLOY,
        ATTACK,
        FORTIFY,
        INACTIVE
    }

    enum mainAddressSent {
        TRUE,
        FALSE
    }

    /* Variables */
    // Chainlink VRF Variables
    VRFCoordinatorV2Interface private immutable i_vrfCoordinator;
    uint64 private immutable i_subscriptionId;
    bytes32 private immutable i_gasLane;
    uint32 private immutable i_callbackGasLimit;
    uint16 private constant REQUEST_CONFIRMATIONS = 3;

    // Setup Variables
    uint256 private immutable i_entranceFee;
    address private immutable controls_address;
    address private immutable data_address;

    uint8[4] private territoriesAssigned = [0, 0, 0, 0]; // Used to track if player receives enough territory.
    uint256[] s_randomWordsArray;
    TerritoryState public s_territoryState;
    GameState public s_gameState;
    LobbyState public s_lobbyState;
    address payable[] public s_players;
    address public player_turn;
    mainAddressSent public s_mainSet;
    mapping(address => bool) public duplicateAddresses;
    address payable[] public s_lobbyEntrants;

    /* Events */
    event RequestedTerritoryRandomness(uint256 indexed requestId);
    event RequestedTroopRandomness(uint256 indexed requestId);
    event ReceivedRandomWords();
    event GameSetupComplete();
    event PlayerJoinedLobby(address indexed player);
    event GameStarting();
    event WinnerSentFunds(address indexed player);
    event MainReset();

    /* Errors */
    error Transfer___Failed();

    constructor(
        address vrfCoordinatorV2,
        uint64 subscriptionId,
        bytes32 gasLane, // keyHash
        uint32 callbackGasLimit,
        uint256 entranceFee,
        address controls,
        address data
    ) VRFConsumerBaseV2(vrfCoordinatorV2) {
        i_vrfCoordinator = VRFCoordinatorV2Interface(vrfCoordinatorV2);
        i_subscriptionId = subscriptionId;
        i_gasLane = gasLane;
        i_callbackGasLimit = callbackGasLimit;
        i_entranceFee = entranceFee;
        s_lobbyState = LobbyState.OPEN;
        s_gameState = GameState.INACTIVE;
        s_mainSet = mainAddressSent.FALSE;
        controls_address = controls;
        data_address = data;
    }

    /* Modifiers */

    modifier onlyPlayer() {
        require(msg.sender == IControls(controls_address).getPlayerTurn());
        console.log("player_turn", IControls(controls_address).getPlayerTurn());
        _;
    }

    modifier onlyControls() {
        require(msg.sender == controls_address);
        _;
        //testttt
    }

    /* Functions */

    function enterLobby() public payable {
        require(msg.value >= i_entranceFee, "Send More to Enter Lobby");
        require(s_lobbyState == LobbyState.OPEN, "Lobby is full"); // require or if statement?
        require(duplicateAddresses[msg.sender] == false, "You've already entered the game!");
        s_players.push(payable(msg.sender));
        s_lobbyEntrants.push(payable(msg.sender));
        emit PlayerJoinedLobby(msg.sender);
        duplicateAddresses[msg.sender] = true;
        if (s_players.length == 4) {
            s_lobbyState = LobbyState.CLOSED;
            emit GameStarting();
            requestRandomness(42);
        }
    }

    // call this function as soon as contract is deployed
    function setMainAddress() public {
        require(s_mainSet == mainAddressSent.FALSE, "Controls contract has already received Main address");
        IControls(controls_address).set_main_address(address(this));
        s_mainSet = mainAddressSent.TRUE;
    }

    function requestRandomness(uint32 num_words) private {
        uint256 requestId = i_vrfCoordinator.requestRandomWords(
            i_gasLane,
            i_subscriptionId,
            REQUEST_CONFIRMATIONS,
            i_callbackGasLimit,
            num_words
        );
        if (s_territoryState == TerritoryState.COMPLETE) {
            emit RequestedTroopRandomness(requestId);
        } else {
            emit RequestedTerritoryRandomness(requestId);
        }
    }

    function fulfillRandomWords(
        uint256, /* requestId */
        uint256[] memory randomWords
    ) internal override {
        s_randomWordsArray = randomWords;
        emit ReceivedRandomWords();
        if (s_territoryState == TerritoryState.INCOMPLETE) {
            assignTerritory(randomWords);
        } else {
            assignTroops(randomWords);
        }
    }

    /**
     * Function receives array of 42 random words which are then used to assign each territory (0-41) an owner (0-3).
     * Mutates a globally declared array s_territories.
     */
    function assignTerritory(uint256[] memory randomWords) private {
        uint8[4] memory playerSelection = [0, 1, 2, 3]; // Eligible players to be assigned territory, each is popped until no players left to receive.
        uint8 territoryCap = 10; // Initial cap is 10, moves up to 11 after two players assigned 10.
        uint8 remainingPlayers = 4; // Ticks down as players hit their territory cap
        uint256 indexAssignedTerritory; // Index of playerSelection that contains a list of eligible players to receive territory.
        uint8 playerAwarded; // Stores the player to be awarded territory, for pushing into the s_territories array.'
        for (uint256 i = 0; i < randomWords.length; i++) {
            indexAssignedTerritory = randomWords[i] % remainingPlayers; // Calculates which index from playerSelection will receive the territory
            playerAwarded = playerSelection[indexAssignedTerritory]; // Player to be awarded territory
            IControls(controls_address).push_to_territories(playerAwarded);
            territoriesAssigned[playerAwarded]++;
            if (territoriesAssigned[playerAwarded] == territoryCap) {
                delete playerSelection[playerAwarded]; // Removes awarded player from the array upon hitting territory cap.
                remainingPlayers--;
                if (remainingPlayers == 2) {
                    territoryCap = 11; // Moves up instead of down, to remove situation where the cap goes down and we have players already on the cap then receiving too much territory.
                }
            }
        }
        s_territoryState = TerritoryState.COMPLETE;
        requestRandomness(78);
    }

    function assignTroops(uint256[] memory randomWords) private {
        uint256 randomWordsIndex = 0;
        // s_territories.length == 42
        // playerTerritoryIndexes.length == 10 or 11
        for (uint256 i = 0; i < 4; i++) {
            uint256[] memory playerTerritoryIndexes = new uint256[](territoriesAssigned[i]); // Initializes array of indexes for territories owned by player i
            uint256 index = 0;
            for (uint256 j = 0; j < 42; j++) {
                if (IControls(controls_address).get_territory_owner(j) == i) {
                    playerTerritoryIndexes[index++] = j;
                }
            }

            for (uint256 j = 0; j < 30 - territoriesAssigned[i]; j++) {
                uint256 territoryAssignedTroop = randomWords[randomWordsIndex++] % territoriesAssigned[i];
                IControls(controls_address).add_troop_to_territory(playerTerritoryIndexes[territoryAssignedTroop]);
            }
        }
        emit GameSetupComplete();
        s_gameState = GameState.DEPLOY;
        IControls(controls_address).set_players(s_players);
    }

    function deploy(uint8 amountToDeploy, uint8 location) public onlyPlayer {
        console.log("deploying");
        require(s_gameState == GameState.DEPLOY, "It is currently not deploy phase!");
        console.log("at end of deploy function");
        require(
            amountToDeploy <= IControls(controls_address).get_troops_to_deploy(),
            "You do not have that many troops to deploy!"
        );
        console.log("at end of deploy function");
        bool troopsLeft = IControls(controls_address).deploy_control(amountToDeploy, location);
        console.log("at end of deploy function");
        if (troopsLeft == false) {
            s_gameState = GameState.ATTACK;
        }
    }

    function attack(
        uint8 territoryOwned,
        uint8 territoryAttacking,
        uint256 troopQuantity
    ) public onlyPlayer {
        require(IControls(controls_address).getAttackStatus() == false);
        require(s_gameState == GameState.ATTACK, "It is currently not attack phase!");
        IControls(controls_address).attack_control(territoryOwned, territoryAttacking, troopQuantity);
    }

    // player clicks this button when they have finished attacking
    function finishAttack() public onlyPlayer {
        require(IControls(controls_address).getAttackStatus() == false);
        s_gameState = GameState.FORTIFY;
    }

    function fortify(
        uint8 territoryMovingFrom,
        uint8 territoryMovingTo,
        uint256 troopsMoving
    ) public onlyPlayer {
        console.log("inside main");
        require(s_gameState == GameState.FORTIFY, "It is currently not fortify phase!");
        console.log("here");
        require(
            IControls(controls_address).fortify_control(territoryMovingFrom, territoryMovingTo, troopsMoving) == true,
            "Your fortification attempt failed"
        );
        s_gameState = GameState.DEPLOY;
    }

    function payWinner(address winner) public onlyControls returns (bool) {
        (bool success, ) = winner.call{value: address(this).balance}("");
        if (!success) {
            revert Transfer___Failed();
        }
        emit WinnerSentFunds(winner);
        resetMain();
        return success;
    }

    /** Getter Functions */

    function getRandomWordsArray() public view returns (uint256[] memory) {
        return s_randomWordsArray;
    }

    function getRandomWordsArrayIndex(uint256 index) public view returns (uint256) {
        return s_randomWordsArray[index];
    }

    function getSubscriptionId() public view returns (uint64) {
        return i_subscriptionId;
    }

    function getGasLane() public view returns (bytes32) {
        return i_gasLane;
    }

    function getCallbackGasLimit() public view returns (uint32) {
        return i_callbackGasLimit;
    }

    function getTerritoryState() public view returns (TerritoryState) {
        return s_territoryState;
    }

    function getRequestConfirmations() public pure returns (uint256) {
        return REQUEST_CONFIRMATIONS;
    }

    function getPlayer(uint256 index) public view returns (address) {
        return s_players[index];
    }

    function getNumberOfPlayers() public view returns (uint256) {
        return s_players.length;
    }

    function getEntranceFee() public view returns (uint256) {
        return i_entranceFee;
    }

    function getLobbyState() public view returns (LobbyState) {
        return s_lobbyState;
    }

    // Resets everything
    function resetMain() internal {
        s_lobbyState = LobbyState.OPEN;
        s_players = new address payable[](0);
        s_gameState = GameState.INACTIVE;
        territoriesAssigned = [0, 0, 0, 0];
        for (uint256 i = 0; i < s_lobbyEntrants.length; i++) {
            duplicateAddresses[s_lobbyEntrants[i]] = false;
        }
        emit MainReset();
    }
}

